import React from "react";
import Card from '@mui/material/Card'
import './MediaInfoi.css';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { CardActionArea, List, ListItem, ListItemText } from "@mui/material";

class MediaInfo extends React.Component {
    constructor(props) {
        super(props)
        this.state = this.props.card
    }
    render() {
        return (
            <Card sx={{ maxWidth: 345 }}>
                <CardMedia
                    sx={{ height: 140 }}
                    image={this.state.mediaImagePath}
                    title="mediaCover"
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {this.state.title}
                    </Typography>
                    <List>
                        <ListItem>
                            <ListItemText
                                primary="Rating"
                                secondary={this.state.rating}
                            />
                        </ListItem>,
                        <ListItem>
                            <ListItemText
                                primary="Community Review"
                                secondary={this.state.communityReview}
                            />
                        </ListItem>,
                        <ListItem>
                            <ListItemText
                                primary="Community Review"
                                secondary={this.state.communityReview}
                            />
                        </ListItem>
                    </List>
                </CardContent>
            </Card>
        )
    }
}

export default MediaInfo;
